// src/components/AdminPage.js

import React, { useEffect, useState } from 'react';
import { useHistory } from 'react-router-dom'; // Ensure you're using react-router-dom v5.x
import jwt_decode from 'jwt-decode';
import 'bootstrap/dist/css/bootstrap.min.css';
import axios from 'axios';

const AdminPage = () => {
  const history = useHistory(); // Initialize useHistory to use for navigation
  const [users, setUsers] = useState([]); // State to hold user data
  const [loading, setLoading] = useState(true); // State to handle loading status
  const [error, setError] = useState(null); // State to handle errors

  // Check user authentication and role
  useEffect(() => {
    const token = localStorage.getItem('token'); // Get the token from localStorage

    if (token) {
      const decodedToken = jwt_decode(token); // Decode the JWT token

      if (decodedToken.user.role !== 'admin') {
        history.push('/'); // Redirect non-admin users to the home page
      } else {
        fetchUsers(); // Fetch users if the authenticated user is an admin
      }
    } else {
      history.push('/login'); // Redirect to login if not authenticated
    }
  }, [history]); // Dependency array to rerun effect if history changes

  // Fetch users from backend API
  const fetchUsers = async () => {
    try {
      const response = await axios.get('/api/users'); // Adjust API endpoint as needed
      setUsers(response.data); // Set users state with fetched data
      setLoading(false); // Set loading to false once data is fetched
    } catch (err) {
      setError('Failed to fetch users'); // Handle fetch error
      setLoading(false); // Set loading to false in case of an error
    }
  };

  // Handle user deletion
  const handleDeleteUser = async (userId) => {
    try {
      await axios.delete(`/api/users/${userId}`); // Adjust API endpoint as needed
      setUsers(users.filter((user) => user._id !== userId)); // Remove deleted user from the list
    } catch (err) {
      setError('Failed to delete user'); // Handle delete error
    }
  };

  // Render loading state
  if (loading) {
    return <div>Loading...</div>;
  }

  // Render error state
  if (error) {
    return <div>{error}</div>;
  }

  // Render admin content and user management table
  return (
    <div className="container mt-5">
      <h2 className="text-center mb-4">Admin Dashboard</h2>
      <table className="table table-bordered">
        <thead className="table-dark">
          <tr>
            <th>Name</th>
            <th>Email</th>
            <th>Role</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {users.map((user) => (
            <tr key={user._id}>
              <td>{user.name}</td>
              <td>{user.email}</td>
              <td>{user.role}</td>
              <td>
                <button
                  className="btn btn-danger"
                  onClick={() => handleDeleteUser(user._id)}
                >
                  Delete
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default AdminPage;
