import React, { useState } from 'react';
import axios from 'axios';

function OrderForm() {
  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    addressLine1: '',
    addressLine2: '',
    addressLine3: '',
    addressLine4: '',
    pincode: '',
    phone: '',
    cartItems: [], // Assuming cart items are managed separately
    totalAmount: 0, // Assuming total amount is calculated
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      // Make API call to submit the order
      const response = await axios.post('http://localhost:5000/submit-order', {
        userDetails: {
          firstName: formData.firstName,
          lastName: formData.lastName,
          addressLine1: formData.addressLine1,
          addressLine2: formData.addressLine2,
          addressLine3: formData.addressLine3,
          addressLine4: formData.addressLine4,
          pincode: formData.pincode,
          phone: formData.phone,
        },
        cartItems: formData.cartItems,
        totalAmount: formData.totalAmount,
      });

      console.log(response.data);
    } catch (error) {
      console.error('Error submitting order:', error);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <input
        type="text"
        name="firstName"
        value={formData.firstName}
        onChange={handleInputChange}
        placeholder="First Name"
        required
      />
      <input
        type="text"
        name="lastName"
        value={formData.lastName}
        onChange={handleInputChange}
        placeholder="Last Name"
        required
      />
      <input
        type="text"
        name="addressLine1"
        value={formData.addressLine1}
        onChange={handleInputChange}
        placeholder="Address Line 1"
        required
      />
      <input
        type="text"
        name="addressLine2"
        value={formData.addressLine2}
        onChange={handleInputChange}
        placeholder="Address Line 2"
      />
      <input
        type="text"
        name="addressLine3"
        value={formData.addressLine3}
        onChange={handleInputChange}
        placeholder="Address Line 3"
      />
      <input
        type="text"
        name="addressLine4"
        value={formData.addressLine4}
        onChange={handleInputChange}
        placeholder="Address Line 4"
      />
      <input
        type="text"
        name="pincode"
        value={formData.pincode}
        onChange={handleInputChange}
        placeholder="Pincode"
        required
      />
      <input
        type="text"
        name="phone"
        value={formData.phone}
        onChange={handleInputChange}
        placeholder="Phone"
        required
      />
      <button type="submit">Submit Order</button>
    </form>
  );
}

export default OrderForm;
