function BrandPage() {
  const [cartCount, setCartCount] = useState(0);

  const addToCart = () => {
    setCartCount(cartCount + 1);
  };

  const navigateToCartPage = () => {
    window.location.href = 'cart.html'; // Replace with the actual path to your cart page
  };

  return (
    <div className="brand-page">
      {/* Search Bar Container with Cart Icon */}
      <div className="search-bar-container">
        <div className="cart-icon" onClick={navigateToCartPage}>
          ðŸ›’ <span id="cart-count">{cartCount}</span>
        </div>
        <input
          type="text"
          className="search-input"
          placeholder="Search products..."
        />
      </div>

      {/* Products Container */}
      <div className="products-container">
        {products.map((product) => (
          <div key={product.id} className="product-card">
            <h3>{product.name}</h3>
            <img src={require(`../assets/${product.img}`)} alt={product.name} />
            <div className="product-options">
              <div className="size-select">
                <label>Size</label>
                <select>
                  {product.sizes.map((option, index) => (
                    <option key={index} value={option.size}>
                      {option.size} Rs.{option.price}
                    </option>
                  ))}
                </select>
              </div>
              <div className="quantity">
                <label>Qty</label>
                <select>
                  {[...Array(10).keys()].map(num => (
                    <option key={num} value={num + 1}>{num + 1}</option>
                  ))}
                </select>
              </div>
            </div>
            <button className="add-to-cart" onClick={addToCart}>Add to Cart</button>
          </div>
        ))}
      </div>
    </div>
  );
}

export default BrandPage;
