// src/contexts/AuthContext.js

import React, { createContext, useState, useContext, useEffect } from 'react';

// Create Context
const AuthContext = createContext();

// Create Provider Component
export function AuthProvider({ children }) {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [clientOnlyMode, setClientOnlyMode] = useState(false);

  useEffect(() => {
    // Check if running in client-only mode (no backend)
    if (!window.navigator.onLine) {
      setClientOnlyMode(true);
      setIsLoggedIn(true); // Assume logged in if client-only
    }
  }, []);

  const login = () => setIsLoggedIn(true);
  const logout = () => {
    setIsLoggedIn(false);
    if (!clientOnlyMode) {
      // If not in client-only mode, reset clientOnlyMode
      setClientOnlyMode(false);
    }
  };

  return (
    <AuthContext.Provider value={{ isLoggedIn, clientOnlyMode, login, logout }}>
      {children}
    </AuthContext.Provider>
  );
}

// Custom Hook to use Auth Context
export function useAuth() {
  return useContext(AuthContext);
}
