// src/components/InvoicePage.js

import React, { useEffect, useState } from 'react';
import './InvoicePage.css';
import 'bootstrap/dist/css/bootstrap.min.css';

function InvoicePage() {
  const [userDetails, setUserDetails] = useState({});
  const [cartItems, setCartItems] = useState([]);

  useEffect(() => {
    // Fetch user details from local storage
    const storedUserDetails = JSON.parse(localStorage.getItem('userDetails'));
    setUserDetails(storedUserDetails);

    // Fetch cart items from local storage
    const storedCartItems = JSON.parse(localStorage.getItem('cartItems')) || [];
    setCartItems(storedCartItems);
  }, []);

  const calculateTotalPrice = () => {
    return cartItems.reduce((total, item) => {
      const price = item.sizes.find((size) => size.size === item.selectedSize).price;
      return total + price * item.quantity;
    }, 0);
  };

  return (
    <div className="container mt-5">
      <h2 className="text-center mb-4">Invoice</h2>
      
      <div className="card mb-4">
        <div className="card-body">
          <h3 className="card-title">User Details</h3>
          <p className="card-text"><strong>First Name:</strong> {userDetails.firstName}</p>
          <p className="card-text"><strong>Last Name:</strong> {userDetails.lastName}</p>
          <p className="card-text"><strong>Gender:</strong> {userDetails.gender}</p>
          <p className="card-text"><strong>Age:</strong> {userDetails.age}</p>
          <p className="card-text"><strong>Email:</strong> {userDetails.email}</p>
          <p className="card-text"><strong>Address Line 1:</strong> {userDetails.addressLine1}</p>
          <p className="card-text"><strong>Address Line 2:</strong> {userDetails.addressLine2}</p>
          <p className="card-text"><strong>Pincode:</strong> {userDetails.pincode}</p>
          <p className="card-text"><strong>Country:</strong> {userDetails.country}</p>
          <p className="card-text"><strong>Phone:</strong> {userDetails.phone}</p>
        </div>
      </div>

      <div className="card">
        <div className="card-body">
          <h3 className="card-title">Order Details</h3>
          {cartItems.length === 0 ? (
            <p className="card-text">No items in the cart.</p>
          ) : (
            <div>
              {cartItems.map((item, index) => (
                <div key={index} className="cart-item mb-3">
                  <h4>{item.name}</h4>
                  <p><strong>Size:</strong> {item.selectedSize}</p>
                  <p><strong>Quantity:</strong> {item.quantity}</p>
                  <p><strong>Price:</strong> Rs.{item.sizes.find(size => size.size === item.selectedSize).price * item.quantity}</p>
                </div>
              ))}
              <div className="total-price mt-4">
                <h4>Total Price: Rs.{calculateTotalPrice()}</h4>
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

export default InvoicePage;
