import React, { useState, useEffect } from 'react';
import './TraceOrderPage.css'; // Import the CSS file for additional styling
import 'bootstrap/dist/css/bootstrap.min.css'; // Import Bootstrap CSS

function TraceOrderPage() {
  const [showStatus, setShowStatus] = useState(false);
  const [timer, setTimer] = useState(120); // Initial countdown timer set to 2 minutes
  const [deliveryDate, setDeliveryDate] = useState(null);

  useEffect(() => {
    // Fetch the delivery date from localStorage (or API if available)
    const storedOrder = JSON.parse(localStorage.getItem('order_1234')); // Replace '1234' with the actual order ID
    if (storedOrder && storedOrder.deliveryDate) {
      setDeliveryDate(storedOrder.deliveryDate);
    }

    let interval = null;
    if (showStatus && timer > 0) {
      interval = setInterval(() => {
        setTimer(prevTimer => prevTimer - 1);
      }, 1000);
    } else if (timer === 0) {
      clearInterval(interval);
    }
    return () => clearInterval(interval);
  }, [showStatus, timer]);

  const handleDeliveryStatus = () => {
    setShowStatus(true);
  };

  return (
    <div className="trace-order-page d-flex flex-column justify-content-center align-items-center min-vh-100">
      <div className="container text-center">
        <h1 className="success-message mb-4">Hurray! Order Placed Successfully!</h1>
        <div className="checkmark-container mb-4">
          <svg
            className="checkmark"
            xmlns="http://www.w3.org/2000/svg"
            viewBox="0 0 24 24"
            width="100"
            height="100"
          >
            <path
              fill="none"
              stroke="#28a745"
              strokeWidth="2"
              d="M5 13l4 4L19 7"
            />
          </svg>
        </div>
        <p className="lead mb-4">Thank you for your purchase. We will process your order soon.</p>
        
        {/* Display the confirmed delivery date */}
        {deliveryDate && (
          <p className="text-info">
            Estimated Delivery Date: <strong>{deliveryDate}</strong>
          </p>
        )}

        <button className="btn btn-primary" onClick={handleDeliveryStatus}>Delivery Status</button>
        {showStatus && (
          <div className="status-overlay">
            <div className="pending-animation"></div>
            <p className="status-message">Please wait for {Math.floor(timer / 60)}:{timer % 60 < 10 ? `0${timer % 60}` : timer % 60} minutes</p>
          </div>
        )}
      </div>
    </div>
  );
}

export default TraceOrderPage;
