// src/components/Order.js
import React, { useState } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import './Order.css';

function Order() {
  const location = useLocation();
  const navigate = useNavigate();

  // Retrieve product details passed from the Product page
  const { img, alt, label, marketPrice, offerPrice } = location.state || {};

  const [quantity, setQuantity] = useState(1);

  const handleQuantityChange = (amount) => {
    setQuantity((prevQuantity) => Math.max(1, prevQuantity + amount));
  };

  const addToCart = () => {
    const product = { img, alt, label, offerPrice, quantity };
    navigate('/cart', { state: { product } });
  };

  return (
    <div className="order-container">
      <div className="product-image">
        <img src={img} alt={alt} />
      </div>
      <h2>{label}</h2>
      <div className="price-details">
        <p>Market Price: ${marketPrice}</p>
        <p>Offer Price: ${offerPrice}</p>
      </div>
      <div className="quantity-controls">
        <button onClick={() => handleQuantityChange(-1)}>-</button>
        <span>{quantity}</span>
        <button onClick={() => handleQuantityChange(1)}>+</button>
      </div>
      <button className="add-to-cart" onClick={addToCart}>
        Add to Cart
      </button>
    </div>
  );
}

export default Order;
