// src/components/Header.js

import React from 'react';
import { NavLink, useNavigate } from 'react-router-dom';
import logo from '../assets/logo4.jpg';
import { useAuth } from '../contexts/AuthContext'; // Import useAuth
import './Header.css';

function Header() {
  const { isLoggedIn } = useAuth(); // Use auth context
  const [showMessage, setShowMessage] = React.useState(false);
  const [message, setMessage] = React.useState('');
  const navigate = useNavigate();

  const handleNavClick = (event, path) => {
    event.preventDefault(); // Prevent default navigation
    if (isLoggedIn) {
      navigate(path); // Navigate if logged in
    } else {
      setMessage('Please log in to continue.');
      setShowMessage(true); // Show message if not logged in
      setTimeout(() => setShowMessage(false), 3000); // Hide message after 3 seconds
    }
  };

  return (
    <header className="header">
      <div className="logo-auth-container">
        <div className="logo">
          <img src={logo} alt="Company Logo" />
        </div>
        <nav className="navigation">
          <NavLink
            to="/"
            className="nav-link"
            onClick={(event) => handleNavClick(event, '/')}
          >
            HOME
          </NavLink>
          <NavLink
            to="/products"
            className="nav-link"
            onClick={(event) => handleNavClick(event, '/products')}
          >
            PRODUCTS
          </NavLink>
          <NavLink
            to="/brands"
            className="nav-link"
            onClick={(event) => handleNavClick(event, '/brands')}
          >
            BRANDS
          </NavLink>
          <NavLink
            to="/cart"
            className="nav-link"
            onClick={(event) => handleNavClick(event, '/cart')}
          >
            CART
          </NavLink>
        </nav>
        <div className="auth-links">
          <a href="/login">LOGIN</a>
          <a href="/signup">/SIGNUP</a>
        </div>
      </div>

      {/* Message Display */}
      {showMessage && (
        <div className="message">
          {message}
        </div>
      )}
    </header>
  );
}

export default Header;
