import React from 'react';
import { BrowserRouter as Router, Routes, Route, useLocation } from 'react-router-dom';
import SignUp from './components/SignUp';
import Login from './components/Login'; 
import Header from './components/Header';
import Body from './components/Body';
import Product from './components/Product';
import Cart from './components/Cart';
import BrandPage from './components/BrandPage';
import Footer from './components/Footer';
import OrderConfirmationPage from './components/OrderConfirmationPage';
import InvoicePage from './components/InvoicePage';
import AdminHome from './admin/AdminHome';
import AdminOrdersPage from './admin/AdminOrdersPage';
import PrivateRoute from './components/PrivateRoute'; // Import PrivateRoute
import { AuthProvider, useAuth } from './contexts/AuthContext'; // Import AuthProvider and useAuth
import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap/dist/js/bootstrap.bundle.min'; 
import './App.css';

function App() {
  const location = useLocation();
  const { currentUser } = useAuth(); // Get current user from Auth context

  // Determine if header and footer should be hidden
  const hideHeader = location.pathname === '/signup' || location.pathname === '/login';

  return (
    <div className="App">
      {!hideHeader && <Header isAdmin={currentUser?.isAdmin} />} {/* Pass isAdmin prop */}
      <div className="content-wrapper">
        <Routes>
          {/* Public Routes */}
          <Route path="/signup" element={<SignUp />} />
          <Route path="/login" element={<Login />} />
          <Route path="/" element={<Body />} />
          <Route path="/products" element={<Product />} />
          <Route path="/brands" element={<BrandPage />} />
          <Route path="/cart" element={<Cart />} />
          <Route path="/order-confirmation" element={<OrderConfirmationPage />} />
          <Route path="/invoicepage" element={<InvoicePage />} />

          {/* Admin Routes */}
          <Route 
            path="/admin/home" 
            element={
              <PrivateRoute>
                <AdminHome />
              </PrivateRoute>
            } 
          />
          <Route 
            path="/admin/orders" 
            element={
              <PrivateRoute>
                <AdminOrdersPage />
              </PrivateRoute>
            } 
          />
        </Routes>
      </div>
      {!hideHeader && <Footer />}
    </div>
  );
}

function AppWrapper() {
  return (
    <Router>
      <AuthProvider> {/* Use only AuthProvider */}
        <App />
      </AuthProvider>
    </Router>
  );
}

export default AppWrapper;
