// src/components/Cart.js

import React, { useState, useEffect } from 'react';
import './Cart.css';

function Cart() { // Renamed to match the file name
  const [cartItems, setCartItems] = useState([]);

  useEffect(() => {
    const storedCartItems = JSON.parse(localStorage.getItem('cartItems')) || [];
    setCartItems(storedCartItems);
  }, []);

  const calculateTotalPrice = () => {
    return cartItems.reduce((total, item) => {
      const price = item.sizes.find(size => size.size === item.selectedSize).price;
      return total + price * item.quantity;
    }, 0);
  };

  const handleDelete = (index) => {
    const updatedCartItems = cartItems.filter((_, i) => i !== index);
    setCartItems(updatedCartItems);
    localStorage.setItem('cartItems', JSON.stringify(updatedCartItems));
  };

  const handlePlaceOrder = () => {
    // Redirect to the Order Confirmation page
    window.location.href = '/order-confirmation';
  };

  return (
    <div className="cart-page">
      <h2 className="order-summary-title">Order Summary</h2>
      {cartItems.length === 0 ? (
        <p>Your cart is empty.</p>
      ) : (
        <div className="order-summary">
          {cartItems.map((item, index) => (
            <div key={index} className="cart-item">
              <div className="cart-item-details">
                <h3>{item.name}</h3>
                <p>Size: {item.selectedSize}</p>
                <p>Quantity: {item.quantity}</p>
                <p>Price: Rs.{item.sizes.find(size => size.size === item.selectedSize).price * item.quantity}</p>
              </div>
              <button onClick={() => handleDelete(index)} className="delete-button">Delete</button>
            </div>
          ))}
          <div className="total-price">
            <h3>Total Price: Rs.{calculateTotalPrice()}</h3>
          </div>
          <button onClick={handlePlaceOrder} className="place-order-button">Place Order</button>
        </div>
      )}
    </div>
  );
}

export default Cart;
