// src/components/OrderConfirmationPage.js

import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './OrderConfirmationPage.css';

function OrderConfirmationPage() {
  const [formData, setFormData] = useState({
    firstName: '',
    middleName: '',
    lastName: '',
    address: '',
    phoneNumber: '',
  });

  const navigate = useNavigate();

  const handleInputChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Handle form submission logic here
    console.log('Order placed with details:', formData);

    // Clear form data after placing order
    setFormData({
      firstName: '',
      middleName: '',
      lastName: '',
      address: '',
      phoneNumber: '',
    });

    // Optionally clear cart or other user data from localStorage if needed
    localStorage.removeItem('cartItems');

    // Navigate to home page or a success page after placing order
    navigate('/');
  };

  return (
    <div className="order-confirmation-page">
      <h2>Order Confirmation</h2>
      <form onSubmit={handleSubmit} className="order-form">
        <input 
          type="text" 
          name="firstName" 
          placeholder="First Name" 
          value={formData.firstName} 
          onChange={handleInputChange} 
          required 
        />
        <input 
          type="text" 
          name="middleName" 
          placeholder="Middle Name" 
          value={formData.middleName} 
          onChange={handleInputChange} 
        />
        <input 
          type="text" 
          name="lastName" 
          placeholder="Last Name" 
          value={formData.lastName} 
          onChange={handleInputChange} 
          required 
        />
        <textarea 
          name="address" 
          placeholder="Address" 
          value={formData.address} 
          onChange={handleInputChange} 
          required 
        ></textarea>
        <input 
          type="text" 
          name="phoneNumber" 
          placeholder="Phone Number" 
          value={formData.phoneNumber} 
          onChange={handleInputChange} 
          required 
        />
        <button type="submit" className="submit-order-button">Submit Order</button>
      </form>
    </div>
  );
}

export default OrderConfirmationPage;
