// src/contexts/AuthContext.js

import React, { createContext, useState, useContext } from 'react';

// Create Context
const AuthContext = createContext();

// Create Provider Component
export function AuthProvider({ children }) {
  const [isLoggedIn, setIsLoggedIn] = useState(false); // Example state
  const [showLoginMessage, setShowLoginMessage] = useState(false); // State to manage login message

  const login = () => {
    setIsLoggedIn(true);
    setShowLoginMessage(false); // Hide message upon successful login
  };

  const logout = () => setIsLoggedIn(false);

  const handleContinue = () => {
    if (!isLoggedIn) {
      setShowLoginMessage(true); // Show login message if user is not logged in
      setTimeout(() => setShowLoginMessage(false), 3000); // Auto-hide message after 3 seconds
    }
  };

  return (
    <AuthContext.Provider value={{ isLoggedIn, login, logout, showLoginMessage, handleContinue }}>
      {children}
    </AuthContext.Provider>
  );
}

// Custom Hook to use Auth Context
export function useAuth() {
  return useContext(AuthContext);
}
