// src/components/OrderConfirmationPage.js

import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './OrderConfirmationPage.css';

function OrderConfirmationPage() {
  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    gender: '',
    age: '',
    email: '',
    password: '',
    address: '',
    company: '',
    country: 'Afghanistan',
    phone: '',
  });

  const navigate = useNavigate();

  const handleInputChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Save form data to local storage
    localStorage.setItem('userDetails', JSON.stringify(formData));
    console.log('Form submitted with details:', formData);

    // Clear form data after submission
    setFormData({
      firstName: '',
      lastName: '',
      gender: '',
      age: '',
      email: '',
      password: '',
      address: '',
      company: '',
      country: 'Afghanistan',
      phone: '',
    });

    // Navigate to the invoice page after submission
    navigate('/invoice');
  };

  return (
    <div className="order-confirmation-page">
      <h2 className="user-summary-title">USER DETAILS</h2>
      <form onSubmit={handleSubmit} className="order-form">
        <div className="form-row">
          <div className="form-group">
            <label>First Name *</label>
            <input 
              type="text" 
              name="firstName" 
              value={formData.firstName} 
              onChange={handleInputChange} 
              required 
            />
          </div>
          <div className="form-group">
            <label>Last Name *</label>
            <input 
              type="text" 
              name="lastName" 
              value={formData.lastName} 
              onChange={handleInputChange} 
              required 
            />
          </div>
        </div>
        {/* ... Rest of the form fields ... */}
        <button type="submit" className="submit-order-button">Submit</button>
      </form>
    </div>
  );
}

export default OrderConfirmationPage;
