// src/components/Order.js

import React from 'react';
import { useParams } from 'react-router-dom';
import './Order.css';

// Sample brand images and prices
import brand1 from '../assets/brand1.jpg';
import brand2 from '../assets/brand2.jpg';
import brand3 from '../assets/brand3.jpg';

const brands = [
  { id: 1, img: brand1, marketPrice: 100, offerPrice: 80 },
  { id: 2, img: brand2, marketPrice: 120, offerPrice: 90 },
  { id: 3, img: brand3, marketPrice: 150, offerPrice: 100 },
  // Add more brand data as needed
];

function Order() {
  const { brandId } = useParams();
  const brand = brands.find((b) => b.id === parseInt(brandId, 10));

  return (
    <div className="order-container">
      {brand ? (
        <>
          <div className="brand-image">
            <img src={brand.img} alt={`Brand ${brand.id}`} />
          </div>
          <div className="order-info">
            <p>Market Price: ${brand.marketPrice}</p>
            <p>Offer Price: ${brand.offerPrice}</p>
            <div className="quantity-control">
              <button>-</button>
              <input type="number" value="1" readOnly />
              <button>+</button>
            </div>
          </div>
        </>
      ) : (
        <p>No brand selected.</p>
      )}
    </div>
  );
}

export default Order;
