import React, { createContext, useContext, useState, useEffect } from 'react';

// Create AuthContext
const AuthContext = createContext();

// Custom hook to use the AuthContext
export function useAuth() {
  return useContext(AuthContext);
}

// AuthProvider component
export function AuthProvider({ children }) {
  const [currentUser, setCurrentUser] = useState(null); // State for storing user info
  const [isLoggedIn, setIsLoggedIn] = useState(false);  // State for managing login status

  // Function to handle login (replace this with real login logic)
  const login = (user) => {
    setIsLoggedIn(true);
    setCurrentUser(user); // Set the current user (could be an admin or regular user)
  };

  // Function to handle logout
  const logout = () => {
    setIsLoggedIn(false);
    setCurrentUser(null); // Clear the user information on logout
  };

  // Simulate an admin login on mount (for testing purposes)
  useEffect(() => {
    const adminUser = { email: 'admininstan@gmail.com', isAdmin: true };
    setCurrentUser(adminUser);
    setIsLoggedIn(true);
  }, []);

  const value = {
    currentUser,
    isLoggedIn,
    login,
    logout,
  };

  return <AuthContext.Provider value={value}>{children}</AuthContext.Provider>;
}
