// src/components/InvoicePage.js

import React, { useEffect, useState } from 'react';
import './InvoicePage.css';

function InvoicePage() {
  const [userDetails, setUserDetails] = useState({});
  const [cartItems, setCartItems] = useState([]);

  useEffect(() => {
    // Fetch user details from local storage
    const storedUserDetails = JSON.parse(localStorage.getItem('userDetails'));
    setUserDetails(storedUserDetails);

    // Fetch cart items from local storage
    const storedCartItems = JSON.parse(localStorage.getItem('cartItems')) || [];
    setCartItems(storedCartItems);
  }, []);

  const calculateTotalPrice = () => {
    return cartItems.reduce((total, item) => {
      const price = item.sizes.find(size => size.size === item.selectedSize).price;
      return total + price * item.quantity;
    }, 0);
  };

  return (
    <div className="invoice-page">
      <h2>Invoice</h2>
      <div className="user-details">
        <h3>User Details</h3>
        <p>First Name: {userDetails.firstName}</p>
        <p>Last Name: {userDetails.lastName}</p>
        <p>Gender: {userDetails.gender}</p>
        <p>Age: {userDetails.age}</p>
        <p>Email: {userDetails.email}</p>
        <p>addressLine1: {userDetails.addressLine1}</p>
        <p>addressLine2: {userDetails.addressLine2}</p>
        <p>Pincode: {userDetails.pincode}</p>
        <p>Country: {userDetails.country}</p>
        <p>Phone: {userDetails.phone}</p>
      </div>

      <div className="order-details">
        <h3>Order Details</h3>
        {cartItems.length === 0 ? (
          <p>No items in the cart.</p>
        ) : (
          <div>
            {cartItems.map((item, index) => (
              <div key={index} className="cart-item">
                <h4>{item.name}</h4>
                <p>Size: {item.selectedSize}</p>
                <p>Quantity: {item.quantity}</p>
                <p>Price: Rs.{item.sizes.find(size => size.size === item.selectedSize).price * item.quantity}</p>
              </div>
            ))}
            <div className="total-price">
              <h4>Total Price: Rs.{calculateTotalPrice()}</h4>
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

export default InvoicePage;
