// src/components/BrandPage.js

import React from 'react';
import './BrandPage.css';

const products = [
  { id: 1, name: 'Butter Milk -Spiced', priceRange: '7.76 - 14.00', img: 'product1.jpg', description: 'Nandini Spiced Buttermilk is a refreshing health drink....', size: '160 ml-Tetra Pack', marketPrice: 8.00 },
  { id: 2, name: 'Plain Lassi', price: 20.00, img: 'brand2.jpg', description: 'Nandini Plain Lassi made from curd is a refreshing drink....', size: '200 ml-Tetra Pack', marketPrice: 20.00 },
  { id: 3, name: 'Mango Lassi', price: 25.00, img: 'brand3.jpg', description: 'Nandini Mango Lassi made from fresh curd and added natural mango pulp....', size: '200 ml-Tetra Pack', marketPrice: 25.00 },
  { id: 4, name: 'Milk Shake-Mango', priceRange: '19.40 - 30.00', img: 'product2.jpg', description: 'Nandini Milk Shake is a Smart Homogenised Double Toned UHT processed milk....', size: '200 ml-Tetra Pack', marketPrice: 30.00 },
  // Add more products to make a total of 24
];

function BrandPage() {
  return (
    <div className="brand-page">
      {products.map((product) => (
        <div key={product.id} className="product-card">
          <h3>{product.name}</h3>
          <img src={require(`../assets/${product.img}`)} alt={product.name} />
          <p>Rs. {product.price || product.priceRange}</p>
          <p>{product.description}</p>
          <div className="product-options">
            <div className="size-select">
              <label>Size</label>
              <select>
                <option>{product.size} Rs.{product.price || product.priceRange}</option>
                {/* Add more size options if needed */}
              </select>
            </div>
            <div className="quantity">
              <label>Qty</label>
              <select>
                {[...Array(10).keys()].map(num => (
                  <option key={num} value={num + 1}>{num + 1}</option>
                ))}
              </select>
            </div>
          </div>
          <button className="add-to-cart">Add to Cart</button>
        </div>
      ))}
    </div>
  );
}

export default BrandPage;
