import React, { useState } from 'react';
import { useParams, useLocation } from 'react-router-dom';
import 'bootstrap/dist/css/bootstrap.min.css';
import Calendar from 'react-calendar'; // Import the Calendar component
import 'react-calendar/dist/Calendar.css'; // Import Calendar CSS

function AdminDeliverPage() {
  const { orderId } = useParams(); // Retrieve the order ID from the URL
  const location = useLocation(); // Retrieve order details from state
  const { order } = location.state || {}; // Destructure order from state

  const [productAvailable, setProductAvailable] = useState(false);
  const [locationVerified, setLocationVerified] = useState(false);
  const [date, setDate] = useState(null);
  const [dateConfirmed, setDateConfirmed] = useState(false);

  // Handle product availability check
  const handleCheckAvailability = () => {
    setProductAvailable(true); // Simulate product availability
  };

  // Handle location verification
  const handleVerifyLocation = () => {
    setLocationVerified(true); // Simulate location verification
  };

  // Handle date selection
  const handleDateChange = (newDate) => {
    setDate(newDate);
    setDateConfirmed(false); // Reset date confirmation
  };

  // Handle delivery date confirmation
  const handleConfirmDate = () => {
    if (date) {
      setDateConfirmed(true); // Confirm the selected date
    }
  };

  return (
    <div className="container mt-5">
      <h1>Deliver Order for {order?.userDetails.firstName} {order?.userDetails.lastName}</h1>
      <h5>Order ID: {orderId}</h5>

      <div className="row">
        {/* Flex container to ensure equal height for Product and Location boxes */}
        <div className="d-flex mb-4">
          {/* Product Details & Product Availability Section */}
          <div className="card shadow-sm flex-fill" style={{ minHeight: '300px' }}>
            <div className="card-body">
              <h5 className="card-title">Product Details & Availability</h5>
              <table className="table table-bordered">
                <thead className="table-warning">
                  <tr>
                    <th>Product Name</th>
                    <th>Quantity</th>
                    <th>Size</th>
                  </tr>
                </thead>
                <tbody>
                  {order?.cartItems.map((item, index) => (
                    <tr key={index}>
                      <td>{item.name}</td>
                      <td>{item.quantity}</td>
                      <td>{item.selectedSize}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
              <button className="btn btn-primary mt-3" onClick={handleCheckAvailability}>
                Check Availability
              </button>
              {productAvailable && <span className="ms-3 text-success">&#10003; Product Available</span>}
            </div>
          </div>

          {/* Location Details & Location Verification Section */}
          <div className="card shadow-sm flex-fill ms-3" style={{ minHeight: '300px' }}>
            <div className="card-body">
              <h5 className="card-title">Location Details & Verification</h5>
              <p>Customer Location: {order?.userDetails.addressLine1},</p>
              <p1> {order?.userDetails.addressLine2}</p1>
              <button className="btn btn-warning" onClick={handleVerifyLocation}>
                Verify Location
              </button>
              {locationVerified && <span className="ms-3 text-success">&#10003; Location Verified</span>}
            </div>
          </div>
        </div>
      </div>

      {/* Delivery Date Section */}
      <div className="row justify-content-center">
        <div className="col-md-6 text-center">
          <div className="card shadow-sm">
            <div className="card-body">
              <h5 className="card-title">Pick Delivery Date</h5>
              <Calendar
                onChange={handleDateChange}
                value={date}
                className="mx-auto"
              />
              <button
                className="btn btn-success mt-3"
                onClick={handleConfirmDate}
                disabled={!date} // Disable button if no date is selected
              >
                Pick Delivery Date
              </button>
              {dateConfirmed && (
                <div className="mt-3 text-success">
                  Delivery date {date.toDateString()} available
                </div>
              )}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default AdminDeliverPage;
