// src/components/OrderConfirmationPage.js

import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './OrderConfirmationPage.css';

function OrderConfirmationPage() {
  const [formData, setFormData] = useState({
    firstName: '',
    middleName: '',
    lastName: '',
    address: '',
    phoneNumber: '',
  });

  const [cartItems, setCartItems] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    const storedCartItems = JSON.parse(localStorage.getItem('cartItems')) || [];
    setCartItems(storedCartItems);
  }, []);

  const handleInputChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Handle form submission logic here
    console.log('Order placed with details:', formData);
    console.log('Cart Items:', cartItems);

    // Clear cart after placing order
    localStorage.removeItem('cartItems');
    navigate('/'); // Navigate to home page or a success page after placing order
  };

  const calculateTotalPrice = () => {
    return cartItems.reduce((total, item) => {
      const price = item.sizes.find(size => size.size === item.selectedSize).price;
      return total + price * item.quantity;
    }, 0);
  };

  return (
    <div className="order-confirmation-page">
      <h2>Order Confirmation</h2>
      <form onSubmit={handleSubmit} className="order-form">
        <input type="text" name="firstName" placeholder="First Name" value={formData.firstName} onChange={handleInputChange} required />
        <input type="text" name="middleName" placeholder="Middle Name" value={formData.middleName} onChange={handleInputChange} />
        <input type="text" name="lastName" placeholder="Last Name" value={formData.lastName} onChange={handleInputChange} required />
        <textarea name="address" placeholder="Address" value={formData.address} onChange={handleInputChange} required></textarea>
        <input type="text" name="phoneNumber" placeholder="Phone Number" value={formData.phoneNumber} onChange={handleInputChange} required />
        <button type="submit" className="submit-order-button">Submit Order</button>
      </form>

      <div className="order-summary">
        <h3>Order Details</h3>
        {cartItems.map((item, index) => (
          <div key={index} className="order-item">
            <h4>{item.name}</h4>
            <p>Size: {item.selectedSize}</p>
            <p>Quantity: {item.quantity}</p>
            <p>Price: Rs.{item.sizes.find(size => size.size === item.selectedSize).price * item.quantity}</p>
          </div>
        ))}
        <div className="total-price">
          <h3>Total Price: Rs.{calculateTotalPrice()}</h3>
        </div>
      </div>
    </div>
  );
}

export default OrderConfirmationPage;
