import React, { useState } from 'react';
import './BrandPage.css';

const products = [
  {
    id: 1,
    name: 'Aavin Diet',
    img: 'aavin_diet.png',
    description: 'Aavin Diet is Pasteurised and Homogenised Double Toned Milk.',
    sizes: [
      { size: '500 ml', price: 18 },
    ],
  },
  {
    id: 2,
    name: 'Aavin Green Magic',
    img: 'aavin_green_magic.png',
    description: 'Aavin Green Magic is Pasteurised and Homogenised Standardised Milk.',
    sizes: [
      { size: '225 ml', price: 11 },
    ],
  },
  {
    id: 3,
    name: 'Aavin Nice',
    img: 'aavin_nice.png',
    description: 'Aavin Nice is Pasteurised and Homogenised Toned Milk.',
    sizes: [
      { size: '500 ml', price: 20 },
    ],
  },
  {
    id: 4,
    name: 'Aavin Premium',
    img: 'aavin_premium.png',
    description: 'Aavin Premium is Pasteurised and Homogenised Full Cream Milk.',
    sizes: [
      { size: '500 ml', price: 30 },
    ],
  },
];

function BrandPage() {
  const [searchQuery, setSearchQuery] = useState('');

  const handleSearch = (e) => {
    setSearchQuery(e.target.value.toLowerCase());
  };

  // Filter products based on search query
  const filteredProducts = products.filter(product =>
    product.name.toLowerCase().includes(searchQuery) ||
    product.description.toLowerCase().includes(searchQuery)
  );

  return (
    <div className="brand-page">
      {/* Header */}
      <header className="page-header">
        <h1>Welcome to Our Brand Page</h1>
      </header>

      {/* Search Bar Container */}
      <div className="search-bar-container">
        <input
          type="text"
          className="search-input"
          placeholder="Search products..."
          value={searchQuery}
          onChange={handleSearch}
        />
      </div>

      {/* Product Cards */}
      <div className="product-grid">
        {filteredProducts.map((product) => (
          <div key={product.id} className="product-card">
            <h3>{product.name}</h3>
            <img src={require(`../assets/${product.img}`)} alt={product.name} />
            <p>{product.description}</p>
            <div className="product-options">
              <div className="size-select">
                <label>Size</label>
                <select>
                  {product.sizes.map((option, index) => (
                    <option key={index} value={option.size}>
                      {option.size} Rs.{option.price}
                    </option>
                  ))}
                </select>
              </div>
              <div className="quantity">
                <label>Qty</label>
                <select>
                  {[...Array(10).keys()].map(num => (
                    <option key={num} value={num + 1}>{num + 1}</option>
                  ))}
                </select>
              </div>
            </div>
            <button className="add-to-cart">Add to Cart</button>
          </div>
        ))}
      </div>
    </div>
  );
}

export default BrandPage;
