import React, { useState, useEffect } from 'react';
import axios from 'axios';

function TraceOrderPage() {
  const [order, setOrder] = useState(null);
  const [loading, setLoading] = useState(true);
  const [deliveryDate, setDeliveryDate] = useState(null);

  useEffect(() => {
    const fetchOrderDetails = async () => {
      try {
        const response = await axios.get('/api/orders/:id'); // Adjust with actual endpoint and order ID
        setOrder(response.data);
        setDeliveryDate(response.data.deliveryDate);
        setLoading(false);
      } catch (error) {
        console.error('Failed to fetch order details', error);
      }
    };

    fetchOrderDetails();
  }, []);

  const getRemainingTime = () => {
    if (!deliveryDate) return 'N/A';

    const now = new Date();
    const delivery = new Date(deliveryDate);
    const timeDiff = delivery - now;

    if (timeDiff <= 0) return 'Order Delivered';

    const minutes = Math.floor(timeDiff / (1000 * 60));
    return `${minutes} minutes remaining`;
  };

  return (
    <div className="container mt-5">
      {loading ? (
        <div>Loading...</div>
      ) : (
        <div>
          <h1>Trace Order</h1>
          <h5>Order ID: {order._id}</h5>
          <div className="card shadow-sm">
            <div className="card-body">
              <h5 className="card-title">Delivery Status</h5>
              <div>
                <p>Delivery Date: {deliveryDate ? new Date(deliveryDate).toDateString() : 'Not Set'}</p>
                <p>Time Remaining: {getRemainingTime()}</p>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default TraceOrderPage;
