// src/components/InvoicePage.js

import React, { useEffect, useState } from 'react';
import './InvoicePage.css';
import 'bootstrap/dist/css/bootstrap.min.css';

function InvoicePage() {
  const [userDetails, setUserDetails] = useState({});
  const [cartItems, setCartItems] = useState([]);

  useEffect(() => {
    // Fetch user details from local storage
    const storedUserDetails = JSON.parse(localStorage.getItem('userDetails'));
    setUserDetails(storedUserDetails);

    // Fetch cart items from local storage
    const storedCartItems = JSON.parse(localStorage.getItem('cartItems')) || [];
    setCartItems(storedCartItems);
  }, []);

  const calculateTotalPrice = () => {
    return cartItems.reduce((total, item) => {
      const price = item.sizes.find((size) => size.size === item.selectedSize).price;
      return total + price * item.quantity;
    }, 0);
  };

  return (
    <div className="container mt-5">
      <div className="row">
        <div className="col text-center">
          <h1 className="invoice-title">INVOICE</h1>
        </div>
      </div>
      
      <div className="row mt-4">
        <div className="col-md-6">
          <h5 className="mb-3"><strong>Billed To:</strong></h5>
          <p><strong>First Name:</strong> {userDetails.firstName}</p>
          <p><strong>Last Name:</strong> {userDetails.lastName}</p>
          <p><strong>Gender:</strong> {userDetails.gender}</p>
          <p><strong>Age:</strong> {userDetails.age}</p>
          <p><strong>Email:</strong> {userDetails.email}</p>
          <p><strong>Address Line 1:</strong> {userDetails.addressLine1}</p>
          <p><strong>Address Line 2:</strong> {userDetails.addressLine2}</p>
          <p><strong>Pincode:</strong> {userDetails.pincode}</p>
          <p><strong>Country:</strong> {userDetails.country}</p>
          <p><strong>Phone:</strong> {userDetails.phone}</p>
        </div>
        
        <div className="col-md-6 text-end">
          <h5 className="mb-3"><strong>Order Details:</strong></h5>
          <p><strong>Order Date:</strong> {new Date().toLocaleDateString()}</p>
          <p><strong>Invoice Number:</strong> INV-{Math.floor(Math.random() * 100000)}</p>
        </div>
      </div>

      <div className="row mt-4">
        <div className="col">
          <table className="table table-bordered">
            <thead className="table-dark">
              <tr>
                <th scope="col">#</th>
                <th scope="col">Item Description</th>
                <th scope="col">Size</th>
                <th scope="col">Qty</th>
                <th scope="col">Rate</th>
                <th scope="col">Amount</th>
              </tr>
            </thead>
            <tbody>
              {cartItems.map((item, index) => (
                <tr key={index}>
                  <th scope="row">{index + 1}</th>
                  <td>{item.name}</td>
                  <td>{item.selectedSize}</td>
                  <td>{item.quantity}</td>
                  <td>Rs.{item.sizes.find(size => size.size === item.selectedSize).price}</td>
                  <td>Rs.{item.sizes.find(size => size.size === item.selectedSize).price * item.quantity}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>

      <div className="row mt-4">
        <div className="col-md-6">
          <p>Thank you for your business. Please make the payment by the due date.</p>
        </div>
        <div className="col-md-6 text-end">
          <p><strong>Subtotal:</strong> Rs.{calculateTotalPrice()}</p>
          <p><strong>Tax (10%):</strong> Rs.{(calculateTotalPrice() * 0.10).toFixed(2)}</p>
          <h5><strong>Total Due:</strong> Rs.{(calculateTotalPrice() * 1.10).toFixed(2)}</h5>
        </div>
      </div>
    </div>
  );
}

export default InvoicePage;
