// src/App.js

import React from 'react';
import { BrowserRouter as Router, Routes, Route, useLocation } from 'react-router-dom';
import SignUp from './components/SignUp';
import Login from './components/Login'; 
import Header from './components/Header';
import Body from './components/Body';
import Product from './components/Product';
import Cart from './components/Cart';
import BrandPage from './components/BrandPage';
import Footer from './components/Footer';
import OrderConfirmationPage from './components/OrderConfirmationPage';
import InvoicePage from './components/InvoicePage';
import AdminHome from './admin/AdminHome';
import PrivateRoute from './components/PrivateRoute'; // Import PrivateRoute
import { useAuth } from './contexts/AuthContext'; // Import useAuth hook
import { AuthProvider } from './contexts/AuthContext';
import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap/dist/js/bootstrap.bundle.min'; 
import './App.css';

function App() {
  const location = useLocation();
  const { currentUser } = useAuth(); // Destructure currentUser from useAuth
  const hideHeader = location.pathname === '/signup' || location.pathname === '/login';
  const isAdmin = currentUser && currentUser.isAdmin; // Check if the user is admin

  return (
    <div className="App">
      {!hideHeader && <Header />}
      <div className="content-wrapper">
        <Routes>
          {/* Public Routes */}
          <Route path="/signup" element={<SignUp />} />
          <Route path="/login" element={<Login />} />
          <Route path="/" element={<Body />} />
          <Route path="/products" element={<Product />} />
          <Route path="/order-confirmation" element={<OrderConfirmationPage />} />
          <Route path="/invoicepage" element={<InvoicePage />} />

          {/* Admin Routes */}
          {isAdmin && (
            <>
              <Route path="/admin/home" element={<AdminHome />} />
              <Route path="/admin/products" element={<Product />} />
            </>
          )}

          {/* Redirect or handle any other route cases here if needed */}
        </Routes>
      </div>
      {!hideHeader && <Footer />}
    </div>
  );
}

export default function AppWrapper() {
  return (
    <Router>
      <AuthProvider>
        <App />
      </AuthProvider>
    </Router>
  );
}
