// src/App.js

import React from 'react';
import { BrowserRouter as Router, Routes, Route, useLocation } from 'react-router-dom';
import SignUp from './components/SignUp';
import Login from './components/Login'; 
import Header from './components/Header';
import AdminHeader from './admin/AdminHeader'; // Import the AdminHeader component
import Body from './components/Body';
import Product from './components/Product';
import Cart from './components/Cart';
import BrandPage from './components/BrandPage';
import Footer from './components/Footer';
import OrderConfirmationPage from './components/OrderConfirmationPage';
import InvoicePage from './components/InvoicePage';
import AdminHome from './admin/AdminHome';
import AdminOrdersPages from './admin/AdminOrdersPage';
import PrivateRoute from './components/PrivateRoute'; // Import PrivateRoute
import { AuthProvider, useAuth } from './contexts/AuthContext'; // Import useAuth
import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap/dist/js/bootstrap.bundle.min'; 
import './App.css';

function App() {
  const location = useLocation();
  const { currentUser } = useAuth(); // Get the current user from the Auth context
  const hideHeader = location.pathname === '/signup' || location.pathname === '/login';

  // Determine if the current user is an admin
  const isAdmin = currentUser?.email === 'admininstan@gmail.com';

  return (
    <div className="App">
      {!hideHeader && (isAdmin ? <AdminHeader /> : <Header />)} {/* Conditionally render the header */}
      <div className="content-wrapper">
        <Routes>
          {/* Public Routes */}
          <Route path="/signup" element={<SignUp />} />
          <Route path="/login" element={<Login />} />
          <Route path="/" element={<Body />} />
          <Route path="/products" element={<Product />} />
          <Route path="/brands" element={<BrandPage />} />
          <Route path="/cart" element={<Cart />} />
          <Route path="/order-confirmation" element={<OrderConfirmationPage />} />
          <Route path="/invoicepage" element={<InvoicePage />} />

          {/* Admin Routes with PrivateRoute */}
          <Route 
            path="/admin/home" 
            element={
              <PrivateRoute>
                <AdminHome />
              </PrivateRoute>
            } 
          />
          <Route 
            path="/admin/orders" 
            element={
              <PrivateRoute>
                <AdminOrdersPages />
              </PrivateRoute>
            } 
          />
        </Routes>
      </div>
      {!hideHeader && <Footer />}
    </div>
  );
}

export default function AppWrapper() {
  return (
    <Router>
      <AuthProvider>
        <App />
      </AuthProvider>
    </Router>
  );
}
