import React, { useState, useEffect } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import './AdminHome.css'; // Make sure this file exists for any custom styles

const AdminHome = () => {
  const [happyCustomers, setHappyCustomers] = useState(0);
  const [fulfilledOrders, setFulfilledOrders] = useState(0);
  const [achievements, setAchievements] = useState(0);
  const [successfulJourney, setSuccessfulJourney] = useState(0);

  useEffect(() => {
    // Simulate fetching data
    const incrementCounters = () => {
      let customers = 0, orders = 0, achievementsCount = 0, journey = 0;

      const interval = setInterval(() => {
        if (customers < 1234) customers += 12;
        if (orders < 5678) orders += 56;
        if (achievementsCount < 9) achievementsCount += 1;
        if (journey < 3) journey += 1;

        setHappyCustomers(customers);
        setFulfilledOrders(orders);
        setAchievements(achievementsCount);
        setSuccessfulJourney(journey);

        if (customers >= 1234 && orders >= 5678 && achievementsCount >= 9 && journey >= 3) {
          clearInterval(interval);
        }
      }, 50); // Adjust interval speed as needed
    };

    incrementCounters();
  }, []);

  return (
    <div className="container mt-5">
      <h1 className="text-center mb-4">Dashboard</h1>
      <div className="row">
        <div className="col-md-3">
          <div className="card text-center p-4 stat">
            <h2 className="card-title">Happy Customers</h2>
            <p className="card-text count">{happyCustomers}</p>
          </div>
        </div>
        <div className="col-md-3">
          <div className="card text-center p-4 stat">
            <h2 className="card-title">Fulfilled Orders</h2>
            <p className="card-text count">{fulfilledOrders}</p>
          </div>
        </div>
        <div className="col-md-3">
          <div className="card text-center p-4 stat">
            <h2 className="card-title">Achievements</h2>
            <p className="card-text count">{achievements}</p>
          </div>
        </div>
        <div className="col-md-3">
          <div className="card text-center p-4 stat">
            <h2 className="card-title">Successful Journey</h2>
            <p className="card-text count">{successfulJourney} years</p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default AdminHome;
