import React, { useState } from 'react';
import './BrandPage.css';

const products = [
  {
    id: 1,
    name: 'Aavin Diet',
    img: 'brand1.jpg',
    description: 'Aavin Diet is Pasteurised and Homogenised Double Toned Milk.',
    sizes: [
      { size: '500 ml', price: 18 },
    ],
  },
  {
    id: 2,
    name: 'Aavin Green Magic',
    img: 'brand2.jpg',
    description: 'Aavin Green Magic is Pasteurised and Homogenised Standardised Milk.',
    sizes: [
      { size: '225 ml', price: 11.00 },
      { size: '500 ml', price: 22.00 },
      { size: '5 Litre', price: 220.00 }
    ],
  },
  {
    id: 3,
    name: 'Aavin Nice',
    img: 'brand3.jpg',
    description: 'Aavin Nice is Pasteurised and Homogenised Toned Milk.',
    sizes: [
      { size: '500 ml', price: 20.00 },
      { size: '1000 ml', price: 40.00 },
    ],
  },
  {
    id: 4,
    name: 'Aavin Premium',
    img: 'brand4.jpg',
    description: 'Aavin Premium is Pasteurised and Homogenised Full Cream Milk',
    sizes: [
      { size: '500 ml', price: 30 },
    ],
  },
  {
    id: 5,
    name: 'Amul Cow Milk',
    img: 'brand5.jpg',
    description: 'Amul Milk is the most hygienic liquid milk available in the market',
    sizes: [
      { size: '500 ml', price: 29 },
    ],
  },
  {
    id: 6,
    name: 'Amul Gold Milk',
    img: 'brand6.jpg',
    description: 'Virtually Zero Bacteria. No need to boil.',
    sizes: [
      { size: '500 ml', price: 34 },
    ],
  },
  {
    id: 7,
    name: 'Amul Lactose Free Milk',
    img: 'brand7.jpg',
    description: 'Amul Lactose Free Milk is Pasteurised and Homogenised Full Cream Milk',
    sizes: [
      { size: '500 ml', price: 30 },
    ],
  },
  {
    id: 8,
    name: 'Amul Calci Plus Milk',
    img: 'brand8.jpg',
    description: 'Amul Calci Plus Milk is Pasteurised and Homogenised Full Cream Milk',
    sizes: [
      { size: '500 ml', price: 30 },
    ],
  },
  {
    id: 9,
    name: 'Hatsun Cow Milk',
    img: 'brand9.jpg',
    description: 'Hatsun Cow Milk is the most hygienic liquid milk available in the market',
    sizes: [
      { size: '500 ml', price: 29 },
    ],
  },
  {
    id: 10,
    name: 'Arokya Full Cream Milk',
    img: 'brand10.jpg',
    description: 'Virtually Zero Bacteria. No need to boil.',
    sizes: [
      { size: '500 ml', price: 34 },
    ],
  },
  {
    id: 11,
    name: 'Arokya Toned Milk',
    img: 'brand11.jpg',
    description: 'Arokya Toned Milk is specially created for people with lactose intolerance',
    sizes: [
      { size: '500 ml', price: 30 },
    ],
  },
  {
    id: 12,
    name: 'Arokya Standardized Milk',
    img: 'brand12.jpg',
    description: 'Arokya Standardized Milk',
    sizes: [
      { size: '500 ml', price: 30 },
    ],
  },
];

function BrandPage() {
  const [searchQuery, setSearchQuery] = useState('');

  const handleSearch = (e) => {
    setSearchQuery(e.target.value.toLowerCase());
  };

  // Filter products based on search query
  const filteredProducts = products.filter(product =>
    product.name.toLowerCase().includes(searchQuery) ||
    product.description.toLowerCase().includes(searchQuery)
  );

  return (
    <div className="brand-page">
      <div className="search-bar">
        <input
          type="text"
          placeholder="Search products..."
          value={searchQuery}
          onChange={handleSearch}
        />
      </div>

      {filteredProducts.map((product) => (
        <div key={product.id} className="product-card">
          <h3>{product.name}</h3>
          <img src={require(`../assets/${product.img}`)} alt={product.name} />
          <p>{product.description}</p>
          <div className="product-options">
            <div className="size-select">
              <label>Size</label>
              <select>
                {product.sizes.map((option, index) => (
                  <option key={index} value={option.size}>
                    {option.size} Rs.{option.price}
                  </option>
                ))}
              </select>
            </div>
            <div className="quantity">
              <label>Qty</label>
              <select>
                {[...Array(10).keys()].map(num => (
                  <option key={num} value={num + 1}>{num + 1}</option>
                ))}
              </select>
            </div>
          </div>
          <button className="add-to-cart">Add to Cart</button>
        </div>
      ))}
    </div>
  );
}

export default BrandPage;
