import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom'; // Import useNavigate for navigation
import './Cart.css';

function CartPage() {
  const [cartItems, setCartItems] = useState([]);
  const navigate = useNavigate(); // Initialize useNavigate

  useEffect(() => {
    // Retrieve cart items from localStorage
    const storedCartItems = JSON.parse(localStorage.getItem('cartItems')) || [];
    setCartItems(storedCartItems);
  }, []);

  const calculateTotalPrice = () => {
    return cartItems.reduce((total, item) => {
      const price = item.sizes.find(size => size.size === item.selectedSize).price;
      return total + price;
    }, 0);
  };

  const handleContinueShopping = () => {
    navigate('/brands'); // Navigate to the Brand Page
  };

  const handleRemoveItem = (index) => {
    const updatedCartItems = cartItems.filter((_, i) => i !== index);
    setCartItems(updatedCartItems);
    localStorage.setItem('cartItems', JSON.stringify(updatedCartItems)); // Update localStorage
  };

  return (
    <div className="cart-page">
      <h2 style={{ textDecoration: 'underline' }}>Order Summary</h2>
      {cartItems.length === 0 ? (
        <p>Your cart is empty.</p>
      ) : (
        <div className="order-summary">
          {cartItems.map((item, index) => (
            <div key={index} className="cart-item">
              <h3>{item.name}</h3>
              <p>Size: {item.selectedSize}</p>
              <p>Price: Rs.{item.sizes.find(size => size.size === item.selectedSize).price}</p>
              <button onClick={() => handleRemoveItem(index)}>Delete</button>
            </div>
          ))}
          <div className="total-price">
            <h3>Total Price: Rs.{calculateTotalPrice()}</h3>
          </div>
          <button className="continue-shopping" onClick={handleContinueShopping}>Continue Shopping</button>
        </div>
      )}
    </div>
  );
}

export default CartPage;
