// src/App.js

import React from 'react';
import { BrowserRouter as Router, Routes, Route, useLocation } from 'react-router-dom';
import SignUp from './components/SignUp';
import Login from './components/Login'; 
import Header from './components/Header';
import Body from './components/Body';
import Product from './components/Product';
import Cart from './components/Cart';
import BrandPage from './components/BrandPage';
import Footer from './components/Footer';
import OrderConfirmationPage from './components/OrderConfirmationPage';
import InvoicePage from './components/InvoicePage';
import AdminHome from './admin/AdminHome';
import AdminOrders from './admin/AdminOrdersPage'; // Import AdminOrders component
import PrivateRoute from './components/PrivateRoute'; // Import PrivateRoute
import { AuthProvider } from './contexts/AuthContext';
import Navbar from './components/Navbar';
import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap/dist/js/bootstrap.bundle.min'; 
import './App.css';
import AdminOrdersPage from './admin/AdminOrdersPage';

function App() {
  const location = useLocation();
  const hideHeader = location.pathname === '/signup' || location.pathname === '/login';

  // Adjust userType as needed, possibly fetching it from context or state
  const userType = 'user'; // or 'admin' based on login status

  return (
    <div className="App">
      {/* Navbar is placed outside of Routes but within the main layout */}
      <Navbar userType={userType} />

      {/* Conditionally hide Header based on the current route */}
      {!hideHeader && <Header />}
      <div className="content-wrapper">
        <Routes>
          {/* Public Routes */}
          <Route path="/signup" element={<SignUp />} />
          <Route path="/login" element={<Login />} />
          <Route path="/" element={<Body />} />
          <Route path="/products" element={<Product />} />
          <Route path="/brands" element={<BrandPage />} />
          <Route path="/cart" element={<Cart />} />
          <Route path="/order-confirmation" element={<OrderConfirmationPage />} />
          <Route path="/invoicepage" element={<InvoicePage />} />

          {/* Admin Routes with PrivateRoute */}
          <Route 
            path="/admin/home" 
            element={
              <PrivateRoute>
                <AdminHome />
              </PrivateRoute>
            } 
          />
          <Route 
            path="/admin-orders" 
            element={
              <PrivateRoute>
                <AdminOrdersPage />
              </PrivateRoute>
            } 
          />
        </Routes>
      </div>
      {/* Conditionally hide Footer based on the current route */}
      {!hideHeader && <Footer />}
    </div>
  );
}

export default function AppWrapper() {
  return (
    <Router>
      <AuthProvider>
        <App />
      </AuthProvider>
    </Router>
  );
}
