import React from 'react';
import './BrandPage.css';

const products = [
  {
    id: 1,
    name: 'Aavin Diet',
    priceRange: '7.76 - 14.00',
    img: 'brand1.jpg',
    description: 'Nandini Spiced Buttermilk is a refreshing health drink....',
    sizes: [
      { size: '160 ml-Tetra Pack', price: 7.76 },
      { size: '200 ml-Tetra Pack', price: 10.00 },
      { size: '500 ml-Bottle', price: 14.00 }
    ],
  },
  {
    id: 2,
    name: 'Aavin Green Magic',
    img: 'brand2.jpg',
    description: 'Aavin Green Magic is Pasteurised and Homogenised Standardised Milk. It is hygienically Processed and Packed in ISO 22000:FSMS Certified Dairy',
    sizes: [
      { size: '200 ml-Tetra Pack', price: 20.00 },
      { size: '500 ml-Tetra Pack', price: 35.00 },
      { size: '1 Litre-Tetra Pack', price: 60.00 }
    ],
  },
  {
    id: 3,
    name: 'Aavin Nice',
    price: 25.00,
    img: 'brand3.jpg',
    description: 'Nandini Mango Lassi made from fresh curd and added natural mango pulp....',
    sizes: [
      { size: '200 ml-Tetra Pack', price: 25.00 },
      { size: '500 ml-Tetra Pack', price: 45.00 },
      { size: '1 Litre-Tetra Pack', price: 80.00 }
    ],
  },
  {
    id: 4,
    name: 'Aavin Delite',
    priceRange: '19.40 - 30.00',
    img: 'brand4.jpg',
    description: 'Nandini Milk Shake is a Smart Homogenised Double Toned UHT processed milk....',
    sizes: [
      { size: '200 ml-Tetra Pack', price: 19.40 },
      { size: '500 ml-Tetra Pack', price: 25.00 },
      { size: '1 Litre-Tetra Pack', price: 30.00 }
    ],
  },
  // Add more products as needed
];

function BrandPage() {
  return (
    <div className="brand-page">
      {products.map((product) => (
        <div key={product.id} className="product-card">
          <h3>{product.name}</h3>
          <img src={require(`../assets/${product.img}`)} alt={product.name} />
          <p>Rs. {product.price || product.priceRange}</p>
          <p>{product.description}</p>
          <div className="product-options">
            <div className="size-select">
              <label>Size</label>
              <select>
                {product.sizes.map((option, index) => (
                  <option key={index} value={option.size}>
                    {option.size} Rs.{option.price}
                  </option>
                ))}
              </select>
            </div>
            <div className="quantity">
              <label>Qty</label>
              <select>
                {[...Array(10).keys()].map(num => (
                  <option key={num} value={num + 1}>{num + 1}</option>
                ))}
              </select>
            </div>
          </div>
          <button className="add-to-cart">Add to Cart</button>
        </div>
      ))}
    </div>
  );
}

export default BrandPage;
