import React from 'react';
import './Body.css';
import product1 from '../assets/product1.jpg';
import product2 from '../assets/product2.jpg';
import product3 from '../assets/product3.jpg';
import product4 from '../assets/product4.jpg';
function Body() {

  const notificationStyle = {
    display: 'inline-block',
    fontWeight: 'bold',
    fontSize: '1.2rem',
    margin: 0,
    animation: 'scroll-left 10s linear infinite',
  };    
return (
    <div className="body-wrapper">

      <div className="notification">
        <p style={notificationStyle}>Special Offer: Get 20% off on Below products! Limited time only.</p>
      </div>

          <div className="body-container">
           <div className="image-container">
              <img src={product1} alt="" />
          </div>
          <div className="image-container">
              <img src={product2} alt="" />
          </div>
          <div className="image-container">
              <img src={product3} alt="" />
          </div>
          <div className="image-container">
          <img src={product4} alt="" />
          </div>
      </div>
      <div className="about-section">
        <h2>About Us</h2>
        <p>
          Instant Diaries is a website where you can order your dairy products instantly. Here we provide All brands which are available in Markets.
          The Feature of this website is, here we provide "FRESH MILKS" to the users from 'Small-Scale Dairy Farming' and 'Milk Society'.
          To an extent of Cattle Famer's(local region) welfare and support,we extend their prpducts to the online.
        </p>
      </div>
      </div>
  );
}

export default Body;
