import React, { useState } from 'react';
import './BrandPage.css';
import { useNavigate } from 'react-router-dom';


const products = [
  {
    id: 1,
    name: 'Aavin Diet',
    img: 'brand1.jpg',
    sizes: [
      { size: '500 ml', price:18, },
    ],
  },
  {
    id: 2,
    name: 'Aavin Green Magic',
    img: 'brand2.jpg',
    sizes: [
      { size: '225 ml', price: 11.00 },
      { size: '500 ml', price: 22.00 },
      { size: '5 Litre', price: 220.00 }
    ],
  },
  {
    id: 3,
    name: 'Aavin Nice',
    price: 25.00,
    img: 'brand3.jpg',
    sizes: [
      { size: '500 ml', price: 20.00 },
      { size: '1000 mls', price: 40.00 },
    ],
  },
  {
    id: 4,
    name: 'Aavin Premium',
    img: 'brand4.jpg',
    sizes: [
      { size: '500 ml', price: 30 },
    ],
  },
  {
    id: 5,
    name: 'Amul Cow Milk',
    img: 'brand5.jpg',
    sizes: [
      { size: '500 ml', price: 29 },
    ],
  },
  {
    id: 6,
    name: 'Amul Gold Milk',
    img: 'brand6.jpg',
    sizes: [
      { size: '500 ml', price: 34 },
    ],
  },
  {
    id: 7,
    name: 'Amul Lactose Free Milk',
    img: 'brand7.jpg',
    sizes: [
      { size: '500 ml', price: 30 },
    ],
  },
  {
    id: 8,
    name: 'Amul Calci Plus Milk',
    img: 'brand8.jpg',
    sizes: [
      { size: '500 ml', price: 30 },
    ],
  },
  {
    id: 9,
    name: 'Hatsun Cow Milk',
    img: 'brand9.jpg',
    sizes: [
      { size: '500 ml', price: 29 },
    ],
  },
  {
    id: 10,
    name: 'Arokya Full Cream Milk',
    img: 'brand10.jpg',
    sizes: [
      { size: '500 ml', price: 34 },
    ],
  },
  {
    id:11,
    name: 'Aroky Toned Milk',
    img: 'brand11.jpg',
    sizes: [
      { size: '500 ml', price: 30 },
    ],
  },
  {
    id: 12,
    name: 'Arokya Standardized Milk',
    img: 'brand12.jpg',
    sizes: [
      { size: '500 ml', price: 30 },
    ],
  },
  {
    id: 13,
    name: 'Amul BUffalo Milk',
    img: 'brand13.jpg',
    sizes: [
      { size: '500 ml', price: 34 },
    ],
  },
  {
    id:14,
    name: 'Amul suar skimmed Milk',
    img: 'brand14.jpg',
    sizes: [
      { size: '500 ml', price: 30 },
    ],
  },
  {
    id: 15,
    name: 'Ponlait',
    img: 'brand15.jpg',
    sizes: [
      { size: '500 ml', price: 30 },
    ],
  },
];


function BrandPage() {
  const [searchQuery, setSearchQuery] = useState('');
  const [cartItems, setCartItems] = useState([]);
  const navigate = useNavigate();

  const handleSearch = (e) => {
    setSearchQuery(e.target.value.toLowerCase());
  };

  const addToCart = (product, selectedSize, quantity) => {
    const item = {
      ...product,
      selectedSize,
      quantity,
    };
    setCartItems([...cartItems, item]);
    localStorage.setItem('cartItems', JSON.stringify([...cartItems, item]));
  };

  const handleAddToCartClick = (product) => {
    const selectedSize = document.querySelector(`#size-select-${product.id}`).value;
    const quantity = document.querySelector(`#quantity-select-${product.id}`).value;
    addToCart(product, selectedSize, parseInt(quantity));
  };

  const navigateToCartPage = () => {
    localStorage.setItem('cartItems', JSON.stringify(cartItems));
    navigate('/cart');
  };

  const filteredProducts = products.filter(product =>
    product.name.toLowerCase().includes(searchQuery)
  );

  return (
    <div className="brand-page">
      <div className="search-bar-container">
        <div className="cart-icon" onClick={navigateToCartPage}>
          🛒 <span id="cart-count">{cartItems.length}</span>
        </div>
        <input
          type="text"
          className="search-input"
          placeholder="Search products..."
          value={searchQuery}
          onChange={handleSearch}
        />
      </div>

      <div className="products-container">
        {filteredProducts.map((product) => (
          <div key={product.id} className="product-card">
            <h3>{product.name}</h3>
            <img src={require(`../assets/${product.img}`)} alt={product.name} />
            <div className="product-options">
              <div className="size-select">
                <label>Size</label>
                <select id={`size-select-${product.id}`}>
                  {product.sizes.map((option, index) => (
                    <option key={index} value={option.size}>
                      {option.size} Rs.{option.price}
                    </option>
                  ))}
                </select>
              </div>
              <div className="quantity">
                <label>Qty</label>
                <select id={`quantity-select-${product.id}`}>
                  {[...Array(10).keys()].map(num => (
                    <option key={num} value={num + 1}>{num + 1}</option>
                  ))}
                </select>
              </div>
            </div>
            <button className="add-to-cart" onClick={() => handleAddToCartClick(product)}>Add to Cart</button>
          </div>
        ))}
      </div>
    </div>
  );
}

export default BrandPage;
