import React, { useState, useEffect } from 'react';
import './TraceOrderPage.css'; // Import the CSS file for additional styling
import 'bootstrap/dist/css/bootstrap.min.css'; // Import Bootstrap CSS
import axios from 'axios';

function TraceOrderPage() {
  const [deliveryDate, setDeliveryDate] = useState(null);
  const [remainingTime, setRemainingTime] = useState('');
  const [showStatus, setShowStatus] = useState(false);
  const [timer, setTimer] = useState(120); // Initial countdown timer set to 2 minutes

  useEffect(() => {
    // Fetch the delivery date from the server
    const fetchDeliveryDate = async () => {
      try {
        const response = await axios.get('/api/orders/:id'); // Replace with actual endpoint and order ID
        setDeliveryDate(new Date(response.data.deliveryDate));
      } catch (error) {
        console.error('Failed to fetch delivery date', error);
      }
    };

    fetchDeliveryDate();
  }, []);

  useEffect(() => {
    // Update remaining time every second
    const interval = setInterval(() => {
      if (deliveryDate) {
        const now = new Date();
        const timeDiff = deliveryDate - now;

        if (timeDiff <= 0) {
          setRemainingTime('Order Delivered');
          clearInterval(interval); // Stop the timer if the delivery date is reached
        } else {
          const hours = Math.floor(timeDiff / (1000 * 60 * 60));
          const minutes = Math.floor((timeDiff % (1000 * 60 * 60)) / (1000 * 60));
          const seconds = Math.floor((timeDiff % (1000 * 60)) / 1000);

          setRemainingTime(`${hours}h ${minutes}m ${seconds}s remaining`);
        }
      }
    }, 1000);

    return () => clearInterval(interval); // Clear interval when the component is unmounted
  }, [deliveryDate]);

  // Start a separate timer for the 2-minute countdown when clicking "Delivery Status"
  useEffect(() => {
    let interval = null;
    if (showStatus && timer > 0) {
      interval = setInterval(() => {
        setTimer((prevTimer) => prevTimer - 1);
      }, 1000);
    } else if (timer === 0) {
      clearInterval(interval);
    }
    return () => clearInterval(interval);
  }, [showStatus, timer]);

  const handleDeliveryStatus = () => {
    setShowStatus(true);
  };

  return (
    <div className="trace-order-page d-flex flex-column justify-content-center align-items-center min-vh-100">
      <div className="container text-center">
        <h1 className="success-message mb-4">Hurray! Order Placed Successfully!</h1>
        <div className="checkmark-container mb-4">
          <svg
            className="checkmark"
            xmlns="http://www.w3.org/2000/svg"
            viewBox="0 0 24 24"
            width="100"
            height="100"
          >
            <path
              fill="none"
              stroke="#28a745"
              strokeWidth="2"
              d="M5 13l4 4L19 7"
            />
          </svg>
        </div>
        <p className="lead mb-4">Thank you for your purchase. We will process your order soon.</p>
        
        {deliveryDate ? (
          <>
            <p>Delivery Date: {deliveryDate.toDateString()}</p>
            <p>Time Remaining: {remainingTime}</p>
            <button className="btn btn-primary" onClick={handleDeliveryStatus}>Delivery Status</button>
          </>
        ) : (
          <p>Loading delivery details...</p>
        )}

        {showStatus && (
          <div className="status-overlay">
            <div className="pending-animation"></div>
            <p className="status-message">
              Please wait for {Math.floor(timer / 60)}:{timer % 60 < 10 ? `0${timer % 60}` : timer % 60} minutes
            </p>
          </div>
        )}
      </div>
    </div>
  );
}

export default TraceOrderPage;
