// src/App.js

import React from 'react';
import { BrowserRouter as Router, Routes, Route, useLocation } from 'react-router-dom';
import SignUp from './components/SignUp';
import Login from './components/Login'; 
import Header from './components/Header';
import Body from './components/Body';
import Product from './components/Product';
import Cart from './components/Cart';
import BrandPage from './components/BrandPage';
import Footer from './components/Footer';
import OrderConfirmationPage from './components/OrderConfirmationPage';
import InvoicePage from './components/InvoicePage';
import AdminHome from './admin/AdminHome';
import AdminOrdersPage from './admin/AdminOrdersPage';
import PrivateRoute from './components/PrivateRoute';
import { AuthProvider } from './contexts/AuthContext';
import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap/dist/js/bootstrap.bundle.min';
import './App.css';

function App() {
  const location = useLocation();
  const hideHeader = location.pathname === '/signup' || location.pathname === '/login';

  // Simulating userType; replace this with real authentication logic
  const userType = 'admin'; // or 'user' based on login status

  return (
    <div className="App">
      {!hideHeader && <Header />}
      <div className="content-wrapper">
        <Routes>
          {/* Public Routes */}
          <Route path="/signup" element={<SignUp />} />
          <Route path="/login" element={<Login />} />

          {/* Conditionally render user or admin routes */}
          {userType === 'user' ? (
            <>
              <Route path="/" element={<Body />} />
              <Route path="/products" element={<Product />} />
              <Route path="/brands" element={<BrandPage />} />
              <Route path="/cart" element={<Cart />} />
              <Route path="/order-confirmation" element={<OrderConfirmationPage />} />
              <Route path="/invoicepage" element={<InvoicePage />} />
            </>
          ) : (
            <>
              <Route 
                path="/admin/home" 
                element={
                  <PrivateRoute>
                    <AdminHome />
                  </PrivateRoute>
                } 
              />
              <Route 
                path="/admin/orders" 
                element={
                  <PrivateRoute>
                    <AdminOrdersPage />
                  </PrivateRoute>
                } 
              />
            </>
          )}
        </Routes>
      </div>
      {!hideHeader && <Footer />}
    </div>
  );
}

export default function AppWrapper() {
  return (
    <Router>
      <AuthProvider>
        <App />
      </AuthProvider>
    </Router>
  );
}
