import React, { useState } from 'react';
import { NavLink, useNavigate } from 'react-router-dom';
import logo from '../assets/logo4.jpg';
import './Header.css';

function Header() {
  const [isLoggedIn, setIsLoggedIn] = useState(false); // Example state for user login status
  const [showPopup, setShowPopup] = useState(false);
  const navigate = useNavigate();

  const handleNavClick = (event, path) => {
    event.preventDefault(); // Prevent default navigation
    if (isLoggedIn) {
      navigate(path); // Navigate if logged in
    } else {
      setShowPopup(true); // Show popup if not logged in
    }
  };

  const handleClosePopup = () => {
    setShowPopup(false); // Close popup
  };

  return (
    <header className="header">
      <div className="logo-auth-container">
        <div className="logo">
          <img src={logo} alt="Company Logo" />
        </div>
        <nav className="navigation">
          <NavLink
            to="/"
            className="nav-link"
            onClick={(event) => handleNavClick(event, '/')}
          >
            HOME
          </NavLink>
          <NavLink
            to="/products"
            className="nav-link"
            onClick={(event) => handleNavClick(event, '/products')}
          >
            PRODUCTS
          </NavLink>
          <NavLink
            to="/brands"
            className="nav-link"
            onClick={(event) => handleNavClick(event, '/brands')}
          >
            BRANDS
          </NavLink>
          <NavLink
            to="/cart"
            className="nav-link"
            onClick={(event) => handleNavClick(event, '/cart')}
          >
            CART
          </NavLink>
        </nav>
        <div className="auth-links">
          <a href="/login">LOGIN</a>
          <a href="/signup">SIGNUP</a>
        </div>
      </div>

      {/* Popup Message */}
      {showPopup && (
        <div className="popup">
          <div className="popup-content">
            <p>Please log in to continue.</p>
            <button onClick={handleClosePopup}>Close</button>
          </div>
        </div>
      )}
    </header>
  );
}

export default Header;
